def get_list_of_macros():
    return builds("{}".format, lists(get_one_macro(), min_size=5, max_size=10).map(",".join))


def get_list_of_strings():
    return builds("{}".format, lists(text(alphabet=string.ascii_lowercase, min_size=3, max_size=8), min_size=5, max_size=10).map(",".join))


def get_list_of_scopes():
    return builds("{}".format, lists(sampled_from(["nv-pairs", "dot-nv-pairs", "all-nv-pairs", "rfc3164", "core", "base", "rfc5424", "syslog-proto", "all-macros", "selected-macros", "sdata", "everything"]), min_size=1).map(",".join))


def list_nth():
    return builds('$(list-nth {} "{}")'.format, integers(min_value=0, max_value=5), get_list_of_strings())


def list_tail():
    return builds('$(list-tail "{}")'.format, get_list_of_strings())


def format_json():
    return builds('$(format-json --scope {} --exclude {} --key {})'.format, get_list_of_scopes(), get_list_of_macros(), get_list_of_macros())


def format_cim():
    return just('$(format-cim)')


def format_ewmm():
    return just('$(format-ewmm)')


def format_gelf():
    return just('$(format-gelf)')


def template_functions():
    tf_functions = one_of(list_nth(), list_tail(), format_json(), format_cim(), format_ewmm(), format_gelf())
    return builds("{}".format, tf_functions)


